import React from 'react';
import './App.css';
import Profile from './components/Profile/Profile';
import Header from './components/Header/Header';
import Navbar from './components/NavBar/Navbar';
import Dialogs from './components/Dialogs/Dialogs';
import News from './components/News/News';
import Music from './components/Music/Music';
import Settings from './components/Settings/Settings';
import { BrowserRouter, Route } from "react-router-dom";

class PasswordConfirm extends React.Component {
  constructor(props) {
    super(props)
    this.state = { 
      password: "",
      passwordConfirmed : ""
     }
  }

  render() {
    return (
      <>
      <form>
        <input placeholder="enter password..." type='password' value={this.state.password} onChange={(e) => {this.setState({ password: e.target.value })}}/>
        <input placeholder="enter password again..." type='password' value={this.state.passwordConfirmed} onChange={(e) => {this.setState({ passwordConfirmed: e.target.value }) (e.target.value !== this.state.password) ? (e.target.style.backgroundColor = "red")
        : (e.target.style.backgroundColor = "#fff")}}/>
        <button disabled={!(this.state.password.length > this.props.min && this.state.password === this.state.passwordConfirmed)}>submit</button>
        </form>
      </>
    )
  }
}

const App = () => {
  return (
    <BrowserRouter>
      <div className='app-wrapper'>

        <Header />
        <Navbar />
        <div className='app-wrapper-content'>
          <PasswordConfirm min={2} />
          {/* <Route path="/profile" component={Profile} />
          <Route path="/dialogs" component={Dialogs} />
          <Route path="/news" component={News} />
          <Route path="/music" component={Music} />
          <Route path="/settings" component={Settings} /> */}
        </div>
      </div>
    </BrowserRouter>)
}

export default App;
