  let path = `/dialogs/${props.id}`

  return (<div className={classes.dialog + ' ' + classes.active}>
    <NavLink to={path}>{props.name}</NavLink>
  </div>)
}

const Message = (props) => {

  return (<div className={classes.dialog}>{props.message}</div>)
}

const Dialogs = (props) => {
  return (
    <div>
