import * as axios from "axios";
import React from "react";
import classes from "./Users.module.css";
import userPhoto from "../../assets/images/default_userAvatar.png";

class Users extends React.Component {
  componentDidMount() {
    axios
      .get(
        `https://social-network.samuraijs.com/api/1.0/users?page=${this.props.currentPage}&count=${this.props.pageSize}`
      )
      .then((response) => {
        this.props.setUsers(response.data.items);
        this.props.setTotalUsersCount(response.data.totalCount)
      });
  }

  onPageChanged = (pageNumber) => {
    this.props.setCurrentPage(pageNumber);
    axios
      .get(
        `https://social-network.samuraijs.com/api/1.0/users?page=${this.props.currentPage}&count=${this.props.pageSize}`
      )
      .then((response) => {
        this.props.setUsers(response.data.items);
      });
  };

  render() {
    let pagesCount = Math.ceil(
      this.props.totalUsersCount / this.props.pageSize
    );

    let pages = [];
    for (let i = 1; i <= pagesCount; i++) {
      pages.push(i);
    }

    return (
      <div>
        <div>
          {pages.map((p) => {
            return (
              <span
                className={this.props.currentPage === p && classes.selectedPage}
                onClick={() => {
                  this.onPageChanged(p);
                }}
              >
                {p}
              </span>
            );
          })}
        </div>

        {this.props.usersData.map((user) => {
          return (
            <div key={user.id} className={classes.usersWrapper}>
              <div>
                <div>
                  <img
                    src={
                      user.photos.small != null ? user.photos.small : userPhoto
                    }
                    alt=""
                    className={classes.usersPhoto}
                  />
                </div>
                <div className={classes.usersButtonsHolder}>
                  {user.followed ? (
                    <button
                      onClick={() => {
                        this.props.unfollow(user.id);
                      }}
                    >
                      unfollow
                    </button>
                  ) : (
                    <button
                      onClick={() => {
                        this.props.follow(user.id);
                      }}
                    >
                      follow
                    </button>
                  )}
                </div>
              </div>
              <div className={classes.infoHolder}>
                <div>
                  <div>Name: {user.name}</div>
                  <div>Bio: {user.status}</div>
                </div>
                <div>
                  <div>Country: {"user.location.country"}</div>
                  <div>city: {"user.location.city"}</div>
                </div>
              </div>
            </div>
          );
        })}
      </div>
    );
  }
}

export default Users;
