import React from "react";
import styles from "./Users.module.css"


let Users = (props) => {

    props.setUsers(
        [
            {
                id: 1,
                photoUrl: "https://animego.org/media/cache/thumbs_180x252/upload/character/5af419a29fcc5239813589.jpg",
                followed: true,
                fullName: "Arseniy",
                status: "i am fullstack developer",
                location: { city: "Kharkow", country: "Ukraine" },
              },
              {
                id: 2,
                photoUrl: "https://animego.org/media/cache/thumbs_180x252/upload/character/5af419a29fcc5239813589.jpg",
                followed: false,
                fullName: "Dimitrii",
                status: "i am frontend developer",
                location: { city: "Magadan", country: "Russia" },
              },
              {
                id: 3,
                photoUrl: "https://animego.org/media/cache/thumbs_180x252/upload/character/5af419a29fcc5239813589.jpg",
                followed: false,
                fullName: "Alex",
                status: "i am backend developer",
                location: { city: "Amur", country: "Russia" },
              },
        ]
    )

  return (
    <div>
      {props.usersData.map((user) => {
       return (<div key={user.id}>
          <div>
            <div>
              <img src={user.photoUrl} alt="" className={styles.usersPhoto}/>
            </div>
            <div>
            {user.followed 
            ? <button onClick={() => {props.unfollow(user.id)}}>unfollow</button> 
            : <button onClick={() => {props.follow(user.id)}}>follow</button>}
            </div>
          </div>
          <div>
            <div>   
              <div>{user.fullName}</div>
              <div>{user.status}</div>
            </div>
            <div>
              <div>{user.location.country}</div>
              <div>{user.location.city}</div>
            </div>
          </div>
        </div>)
      })}
    </div>
  );
};

export default Users;
