import * as axios from "axios";
import React from "react";
import styles from "./Users.module.css";
import userPhoto from '../../assets/images/default_userAvatar.png'

class Users extends React.Component {
  getUsers = () => {
    debugger
    if (this.props.usersData.length === 0) {
      axios
        .get("https://social-network.samuraijs.com/api/1.0/users")
        .then((response) => {
          debugger
          this.props.setUsers(response.data.items);
        });
    }

    return (
      <div>
        {this.props.usersData.map((user) => {
          return (
            <div key={user.id} className={styles.usersWrapper}>
              <div>
                <div>
                  <img src={user.photos.small != null ? user.photos.small : userPhoto} alt="" className={styles.usersPhoto} />
                </div>
                <div className={styles.usersButtonsHolder}>
                  {user.followed ? (
                    <button
                      onClick={() => {
                        this.props.unfollow(user.id);
                      }}
                    >
                      unfollow
                    </button>
                  ) : (
                    <button
                      onClick={() => {
                        this.props.follow(user.id);
                      }}
                    >
                      follow
                    </button>
                  )}
                </div>
              </div>
              <div className={styles.infoHolder}>
                <div>
                  <div>Name: {user.name}</div>
                  <div>Bio: {user.status}</div>
                </div>
                <div>
                  <div>Country: {"user.location.country"}</div>
                  <div>city: {"user.location.city"}</div>
                </div>
              </div>
            </div>
          );
        })}
      </div>
    );
  };
}

export default Users;
