import React from 'react';
import { NavLink } from 'react-router-dom';
import classes from './Dialogs.module.css';
//import { useState } from 'react';

const DialogItem = (props) => {
  let path = `/dialogs/${props.id}`

  return (<div className={classes.dialog + ' ' + classes.active}>
    <NavLink to={path}>{props.name}</NavLink>
  </div>)
}

const Message = (props) => {

  return (<div className={classes.dialog}>{props.message}</div>)
}



const Dialogs = (props) => {

  let usersData = [
    { id: 1, name: "John" },
    { id: 2, name: "Alan" },
    { id: 3, name: "Alex" },
    { id: 4, name: "Sofia" }]

    let dialogsData = [
      { id: 1, message: "hi" },
      { id: 2, message: "hello" },
      { id: 3, message: "how are you" }]

  return (
    <div>
      <div className={classes.dialogs}>
        <div className={classes.dialogsItems}>
          <DialogItem name={usersData[0].name} id={usersData[0].id} />
          <DialogItem name={usersData[1].name} id={usersData[1].id} />
          <DialogItem name={usersData[2].name} id={usersData[2].id} />
          <DialogItem name={usersData[3].name} id={usersData[3].id} />
        </div>
        <div className={classes.messages}>
          <Message message={dialogsData[0].message} id={dialogsData[0].id} />
          <Message message={dialogsData[1].message} id={dialogsData[1].id}/>
          <Message message={dialogsData[2].message} id={dialogsData[2].id}/>
        </div>
      </div>
    </div>
  )
}

