//     <p>
//         {children}
//     </p>
// </div>

const Molodost = ({a, b}) =>
<pre>
    a+b = {a + b}
    {/* <Di4 /> */}
</pre>


const Input = () => {
  const [text, setText] = useState('')
  return (
      <>
      <h1>{text.toUpperCase()}</h1>
      <h2>{text.length}</h2>
      <input value={text}
              onChange={(e) => setText(e.target.value.toLowerCase())}/>
      </>
              ) 
 }

function App() {
  return (
    <div>
      <Molodost a={5} b={10}/>
      <Molodost a={"qwert"} b={"yu"}/>
      <Input />
    </div>
  )

}

export default App;
