let store = {
  _state: {
    //posts

    profilePage: {
      postsData: [
        { id: 1, message: "Hi, i am john", likesCount: 12 },
        { id: 2, message: "welcome", likesCount: 18 },
      ],
      newPostText: "",
    },
    //dialogs
    dialogsPage: {
      dialogsData: [
        { id: 1, name: "John" },
        { id: 2, name: "Alan" },
        { id: 3, name: "Alex" },
        { id: 4, name: "Sofia" },
      ],
      messagesData: [
        { id: 1, message: "hi" },
        { id: 2, message: "hello" },
        { id: 3, message: "how are you" },
      ],
    },
    //sidebar
    sideBar: {},
  },

  getState() {
    return this._state;
  },

  rerenderEntireTree() {
    console.log("state changed");
  },

  addPost() {
    let newPost = {
      id: this.getState().profilePage.postsData.length + 1,
      message: this.postMessage,
      likesCount: 0,
    };

    this.getState().profilePage.postsData.push(newPost);
    this.getState().profilePage.newPostText = "";
    this.rerenderEntireTree(this.getState());
  },

  updateNewPostText(newText) {
    this.getState().profilePage.newPostText = newText;
    this.rerenderEntireTree(this.getState());
  },

  subscribe(callback) {
    this.rerenderEntireTree = callback;
  },
};

