import React from 'react';
import classes from './Dialogs.module.css';


const Dialogs = ({ header = "+", open, children= "+" }) => {
  let childrenResult;
  let headerResult = <div>{header}</div>;

  if (open === true) {
    //  children.children = open
    childrenResult = <div>{children}</div>

    } else {
      childrenResult = <div></div>
    }

  // onclick.headerResult = () => {
  //   (open === true) {
  //     open = false 
  //   } open === false open = true
  // }
  const openOnClick = () => {
    if (open === true) {
      open = false
    } else {
      open = true
    }
  }


  return (
    <>
      <div onclick={this.openOnClick}>{headerResult}</div>;
      {childrenResult};
    </>
  )
}

export default Dialogs;
