import React from "react";
import { NavLink } from "react-router-dom";
import classes from "./Dialogs.module.css";
//import { useState } from 'react';

const UserItem = (props) => {
  let path = `/dialogs/${props.id}`;

  return (
    <div className={classes.dialog + " " + classes.active}>
      <NavLink to={path}>{props.name}</NavLink>
    </div>
  );
};

const Dialog = (props) => {
  return <div className={classes.dialog}>{props.message}</div>;
};

const Dialogs = (props) => {
  let usersData = [
    { id: 1, name: "John" },
    { id: 2, name: "Alan" },
    { id: 3, name: "Alex" },
    { id: 4, name: "Sofia" },
  ];

  let dialogsData = [
    { id: 1, message: "hi" },
    { id: 2, message: "hello" },
    { id: 3, message: "how are you" },
  ];

  let usersElements = usersData.map((user) => (
    <UserItem name={user.name} id={user.id} />
  ));

  let dialogsElements = dialogsData.map((dialog) => (
    <Dialog message={dialog.message} id={dialog.id} />
  ));

  return (
    <div>
      <div className={classes.dialogs}>
        <div className={classes.dialogsItems}>{usersElements}</div>
        <div className={classes.messages}>{dialogsElements}</div>
      </div>
    </div>
  );
};

export default Dialogs;
