import React from "react";
import { connect } from "react-redux";
import { Field, reduxForm } from "redux-form";
import { login } from "../../redux/authReducer";
import { maxLengthCreator, required } from "../../validators";
import { Input } from "../common/FormControls/FormsControls";

const maxLength30 = maxLengthCreator(30);

const LoginForm = (props) => {
  return (
    <form onSubmit={props.handleSubmit}>
      <div>

        <Field placeholder={"Login"} validate={[required, maxLength30]}  name={"login"} component={Input} />
      </div>
      <div>
        <Field placeholder={"Password"} validate={[required, maxLength30]} type={"password"} name={"password"} component={Input} />
      </div>
      <div>
        <Field type={"checkbox"} name={"rememberMe"} component={"input"} />
        remember me
      </div>
      <div>
        <button>Login</button>
      </div>
    </form>
  );
};

const LoginReduxForm = reduxForm({
  // a unique name for the form
  form: "login",
})(LoginForm);

const Login = (props) => {
  const onSubmit = (formData) => {

    props.login(formData.login, formData.password, formData.rememberMe); //чрез санку
  };

  if (props.isLogin) {
    return <Redirect to={"/profile"}/>
  }

  return (
    <div>
      <h1>Login</h1>
      <LoginReduxForm onSubmit={onSubmit} />
    </div>
  );
};

const mapStateToProps = (state) => {
  isLogin: state.auth.isLogin
}

export default connect(mapStateToProps, {login})(Login) //санка
