import React from 'react';
import './App.css';
import Profile from './components/Profile/Profile';
import Header from './components/Header/Header';
import Navbar from './components/NavBar/Navbar';
import Dialogs from './components/Dialogs/Dialogs';
import News from './components/News/News';
import Music from './components/Music/Music';
import Settings from './components/Settings/Settings';
import { BrowserRouter, Route } from "react-router-dom";



class TimerControl extends React.Component {
  constructor(props) {
    super(props)
    this.state = {
      hours: 0,
      minutes: 0,
      sec: 0,

      pause: "false"
    }

    this.pause = this.pause.bind(this);
  }

  secondsToTime(secs) {
  //debugger
    if (this.state.sec != 0) {
      let hoursCalc = Math.floor(secs / (60 * 60))

      let divisor_for_minutes = secs % (60 * 60);
      let minutesCalc = Math.floor(divisor_for_minutes / 60);

      let divisor_for_seconds = divisor_for_minutes % 60;
      let secondsCalc = Math.ceil(divisor_for_seconds);

      this.setState({
        hours: this.hoursCalc,
        minutes: this.minutesCalc,
        sec: this.secondsCalc,
      });
    }
  }
  //

  // hoursToTime(hours) {

  //   let hoursCalc = hours
  //   let secondsCalc = Math.floor(hours * 3600)
  //   let minutesCalc = Math.floor(hours * 60)

  //   //let divisor_for_seconds = divisor_for_minutes % 60;
  //   // let divisor_for_minutes = hours % (60 * 60);
  //   // let minutesCalc = Math.floor(divisor_for_minutes / 60);    
  //   // let secondsCalc = Math.ceil(divisor_for_seconds);
  //   this.setState({
  //     hours: hoursCalc,
  //     minutes: minutesCalc,
  //     sec: secondsCalc,
  //   });
  // }

  pause() {
    let statePause = (this.state.pause === "true") ? "false" : "true"
    this.setState({
      pause: statePause
    });
  }

  tick() {
    if (this.state.pause === "false") {
      this.setState({
        // hours: this.state.hours,
        // minutes: this.state.minutes,
        sec: this.secondsToTime(this.state.sec - 1)
      });
    } else {
      this.setState({
        // hours: this.state.hours,
        // minutes: this.state.minutes,
        sec: this.state.sec
      });
    }
  }


  componentDidMount() {
    let timeLeftVar = this.secondsToTime(this.state.sec);
    this.setState({ sec: timeLeftVar });
    this.timerID = setInterval(
      () => this.tick(),
      1000
    );

  }

  componentWillUnmount() {
    clearInterval(this.timerID);
  }

  render() {
    return (
      <>
        <div>
          <form>
            <input type="number" placeholder="h"  onChange={(e) => {
              this.setState({ hours: e.target.value })
            }} />
            <input type="number" placeholder="m"  onChange={(e) => {
              this.setState({ minutes: e.target.value })
            }} />
            <input type="number" placeholder="s" value={10} onChange={(e) => {
              this.setState({ sec: e.target.value })
            }} />
          </form>
        </div>
        <div>
          <div>Hrs: {this.state.hours} min: {this.state.minutes} sec: {this.state.sec}</div>
          <button onClick={this.pause}>pause/start</button>
        </div>


      </>
    )
  }
}
//Hrs: {this.state.time.h} min: {this.state.time.m} sec: {this.state.time.s}
//{(this.state.sec === 0) ? this.componentWillUnmount() || "0" : this.state.sec}


const App = () => {
  return (
    <BrowserRouter>
      <div className='app-wrapper'>

        <Header />
        <Navbar />
        <div className='app-wrapper-content'>
          <TimerControl />
          {/* <Route path="/profile" component={Profile} />
          <Route path="/dialogs" component={Dialogs} />
          <Route path="/news" component={News} />
          <Route path="/music" component={Music} />
          <Route path="/settings" component={Settings} /> */}
        </div>
      </div>
    </BrowserRouter>)
}

export default App;
