    messagesData: [
      { id: 1, message: "hi" },
      { id: 2, message: "hello" },
      { id: 3, message: "how are you" },
    ],
  },
  //sidebar
  sideBar: {},
};
///
export let addPost = (postMessage) => {
    let newPost = {
        id: state.profilePage.postsData[postsData.length+1],
        message: postMessage,
        likesCount: 0
    }

    state.profilePage.postsData.push(newPost)
}

