import React from 'react';
import classes from './Dialogs.module.css';


// const Dialogs = () => {
//   return (
//       <div>Dialogs</div>
//   )
// }

const Dialogs = ({ header = "+", open, children }) => {
  let childrenResult;

  if (open === true) {
    childrenResult = <div>{children}</div>

  } else {
    childrenResult = <div></div>
  }

  const openOnClick = () => {
    if (open === true) {
      open = false
    } else {
      open = true
    }
  }


  return (
    <>
      <div onClick={() => openOnClick} >{header}</div>;
      {childrenResult};
    </>
  )
}

export default Dialogs;
