const FOLLOW = "FOLlOW";
const UNFOLLOW = "UNFOLLOW";
const SET_USERS = "SET-USERS";

let initialState = {
  usersData: [
    {
      id: 1,
      photoUrl: "https://static.wikia.nocookie.net/new-game/images/0/0b/Th-c8.png/revision/latest/top-crop/width/360/height/450?cb=20180121132207",
      followed: true,
      fullName: "Arseniy",
      status: "i am fullstack developer",
      location: { city: "Kharkow", country: "Ukraine" },
    },
    {
      id: 2,
      photoUrl: "https://static.wikia.nocookie.net/new-game/images/0/0b/Th-c8.png/revision/latest/top-crop/width/360/height/450?cb=20180121132207",
      followed: false,
      fullName: "Dimitrii",
      status: "i am frontend developer",
      location: { city: "Magadan", country: "Russia" },
    },
    {
      id: 3,
      photoUrl: "https://static.wikia.nocookie.net/new-game/images/0/0b/Th-c8.png/revision/latest/top-crop/width/360/height/450?cb=20180121132207",
      followed: false,
      fullName: "Alex",
      status: "i am backend developer",
      location: { city: "Amur", country: "Russia" },
    },
  ],
  newPostText: "",
};

const usersReducer = (state = initialState, action) => {
  switch (action.type) {
    case FOLLOW:
      return {
        ...state,
        usersData: state.usersData.map((user) => {
          if (user.id === action.userId) {
            return { ...user, followed: true };
          }
          return user;
        }),
      };

    case UNFOLLOW:
      return {
        ...state,
        usersData: state.usersData.map((user) => {
          if (user.id === action.userId) {
            return { ...user, followed: false };
          }
          return user;
        }),
      };

    case SET_USERS:
      return {
        ...state,
        usersData: [...state.usersData, ...action.usersData],
      };

    default:
      return state;
  }
};

export const followAC = (userId) => ({ type: FOLLOW, userId });
export const unfollowAC = (userId) => ({ type: UNFOLLOW, userId });
export const setUsersAC = (usersData) => ({ type: SET_USERS, usersData });

export default usersReducer;
