import sidebarReducer from "./sidebarReducer";
import dialogsReducer from "./dialogsReducer";
import profileReducer from "./profileReducer";

const ADD_POST = "ADD-POST";
const ADD_UPDATE_NEW_POST_TEXT = "UPDATE-NEW-POST-TEXT";

const UPDATE_NEW_MESSAGE_BODY = "UPDATE-NEW-MESSAGE-BODY";
const SEND_MESSAGE = "SEND-MESSAGE";

let store = {
  _state: {
    //posts

    profilePage: {
      postsData: [
        { id: 1, message: "Hi, i am john", likesCount: 12 },
        { id: 2, message: "welcome", likesCount: 18 },
      ],
      newPostText: "",
    },
    //dialogs
    dialogsPage: {
      dialogsData: [
        { id: 1, name: "John" },
        { id: 2, name: "Alan" },
        { id: 3, name: "Alex" },
        { id: 4, name: "Sofia" },
      ],
      messagesData: [
        { id: 1, message: "hi" },
        { id: 2, message: "hello" },
        { id: 3, message: "how are you" },
      ],
      newMessageBody: "",
    },
    //sidebar
    sideBar: {},
  },
  _callSubcriber() {
    console.log("state changed");
  },

  getState() {
    return this._state;
  },

  subscribe(callback) {
    this._callSubcriber = callback;
  },

  dispatch(action) {
    this._state.profilePage = profileReducer(this._state.profilePage, action);
    this._state.profilePage = dialogsReducer(this._state.dialogsPage, action);
    this._state.profilePage = sidebarReducer(this._state.sideBar, action);

    this._callSubcriber(this._state);
  },
};

export const addPostActionCreator = () => ({ type: ADD_POST });

export const onPostChangeActionCreator = (text) => ({
  type: ADD_UPDATE_NEW_POST_TEXT,
  newText: text,
});

export const sendMessageCreator = () => ({ type: SEND_MESSAGE });

export const updateNewMessageBodyCreator = (body) => ({
  type: UPDATE_NEW_MESSAGE_BODY,
  body: body,
});

export default store;
