const ADD_POST = "ADD-POST";
const ADD_UPDATE_NEW_POST_TEXT = "UPDATE-NEW-POST-TEXT";

const UPDATE_NEW_MESSAGE_BODY = "UPDATE-NEW-MESSAGE-BODY";
const SEND_MESSAGE = 'SEND-MESSAGE'

let store = {
  _state: {
    //posts

    profilePage: {
      postsData: [
        { id: 1, message: "Hi, i am john", likesCount: 12 },
        { id: 2, message: "welcome", likesCount: 18 },
      ],
      newPostText: "",
    },
    //dialogs
    dialogsPage: {
      dialogsData: [
        { id: 1, name: "John" },
        { id: 2, name: "Alan" },
        { id: 3, name: "Alex" },
        { id: 4, name: "Sofia" },
      ],
      messagesData: [
        { id: 1, message: "hi" },
        { id: 2, message: "hello" },
        { id: 3, message: "how are you" },
      ],
      newMessageBody: ""
    },
    //sidebar
    sideBar: {},
  },
  _callSubcriber() {
    console.log("state changed");
  },

  getState() {
    return this._state;
  },

  subscribe(callback) {
    this._callSubcriber = callback;
  },

  dispatch(action) {
    if (action.type === ADD_POST) {
      let newPost = {
        id: this._state.profilePage.postsData.length + 1,
        message: this._state.profilePage.newPostText,
        likesCount: 0,
      };

      this._state.profilePage.postsData.push(newPost);
      this._state.profilePage.newPostText = "";
      this._callSubcriber(this._state);
    }
    if (action.type === ADD_UPDATE_NEW_POST_TEXT) {
      this._state.profilePage.newPostText = action.newText;
      this._callSubcriber(this._state);
    }
    if (action.type === UPDATE_NEW_MESSAGE_BODY) {
      this._state.dialogsPage.newMessageBody = action.body;
      this._callSubcriber(this._state)
    }
    if (action.type === SEND_MESSAGE) {
      let body = this._state.dialogsPage.newMessageBody;
      this._state.dialogsPage.newMessageBody = "";
      this._state.dialogsPage.message.push();
      this._callSubcriber(this._state)
    }

  },
};

export const addPostActionCreator = () => ({ type: ADD_POST });

export const onPostChangeActionCreator = (text) => ({
  type: ADD_UPDATE_NEW_POST_TEXT,
  newText: text,
});

export default store;
