import logo from './logo.svg';
import './App.css';
import { useState } from 'react';

// const Di4 = ({color, header, children}) =>
// <div style={{color}}>
//     <h1>{header}</h1>
//     <p>
//         {children}
//     </p>
// </div>

const Molodost = ({a, b}) =>
<pre>
    a+b = {a + b}
    {/* <Di4 /> */}
</pre>


const Input = () => {
  const [text, setText] = useState('')
  return (
      <>
      {console.log(text)}
      {text.length > 15 && <h1>{text.toUpperCase()}</h1>}
      <h2>{text.length}</h2>
      <input value={text}
              onChange={(e) => setText(e.target.value.toLowerCase())}/>
      </>
              ) 
 }

//  const Episode = () => 
//  <div>
//    {episodes.map(episode => <Episode episode={episode} />)}
//  </div>

//  const Episodes = () => 
// <div>
//   {episodes.map(episode => <Episode episode={episode} />)}
// </div>

const LoginForm = ({}) => {
  const [login, password, setText] = useState('')
  return (
    <>
      <input placeholder='Login' value={login} onChange={(e) => setText(e.target.value.toLowerCase())}/>
      <input placeholder='Password' value={password} onChange={(e) => setText(e.target.value.toLowerCase())}/>
      {login.length < 1 || password.length < 1 ? <button disabled>Login</button> : <button>Login</button>}
    </>
  )
}

function App() {
  const [counter, setCounter] = useState('')
  return (
    <div className="App">
      <LoginForm />
      <Molodost a={5} b={10}/>
      <Molodost a={"qwert"} b={"yu"}/>
      <Input />
    </div>
  )

}

export default App;
